SELECT n.*, (select typename from wkrj_sys_data_dictionary where id=n.accept_source) source,(select typename from wkrj_sys_data_dictionary where id=n.content_type) type, (select user_realname from wkrj_sys_user where user_id=n.input_user) user_realname,DATEDIFF(NOW(),DATE_SUB(input_time,INTERVAL -deal_days DAY)) remain_days, DATE_FORMAT(DATE_SUB(input_time,INTERVAL -deal_days DAY),'%Y-%m-%d %H:%i:%s') daoqi_time, round((UNIX_TIMESTAMP(NOW())-UNIX_TIMESTAMP(DATE_SUB(input_time,INTERVAL -deal_days DAY)))/3600) remain_hours, (select dept_name from wkrj_sys_department where dept_id=n.chengban_dept) dept_name,(select typename from wkrj_sys_data_dictionary where id=n.business_type) nature, DATE_FORMAT((select opt_time from wkrj_procedurerecord where event_id=n.event_id and opt_type=1 order by opt_time desc limit 1),'%Y-%m-%d %H:%i:%s') reply_time, (select opt_content from wkrj_procedurerecord where event_id=n.event_id and opt_type=1 order by opt_time desc limit 1) reply_content, (select opt_content from wkrj_procedurerecord where event_id=n.event_id and opt_type=2 order by opt_time desc limit 1) delay_reason, DATE_FORMAT((select opt_time from wkrj_procedurerecord where event_id=n.event_id and opt_type=2 order by opt_time desc limit 1),'%Y-%m-%d %H:%i:%s') delay_time, DATE_FORMAT(n.input_time,'%Y-%m-%d %H:%i:%s') event_inputtime,DATE_FORMAT(n.qianshou_time,'%Y-%m-%d %H:%i:%s') qianshoutime FROM `wkrj_reportevent_wf` n where 1=1
and n.input_user = #{user_id}
and n.chengban_dept = #{user_dept}
and n.event_status like concat("%", #{event_status},"%")
and n.event_status != '未完成'
and n.qianshou_status = #{qianshou_status}
and n.cuiban_status = #{cuiban_status}
and n.event_no like concat("%", #{event_no},"%")
and n.event_content like concat("%", #{event_content},"%")
and n.event_no like concat("%", #{dateF},"%")
and n.shenhe_status = #{shenhe_status}
and n.caller_username like concat("%", #{caller_username},"%")
and n.caller_tel like concat("%", #{caller_tel},"%")
order by daoqi_time asc
order by reply_time desc
order by event_inputtime desc
limit #{offset},#{page}

        
