mapper.select:
SELECT must_read_id,commenter,comment_school,must_read_title, comment_content,likenum,DATE_FORMAT(comment_time,'%Y-%m-%d %H:%i:%s') AS comment_time FROM wkrj_messagemanager WHERE 1=1
AND must_read_title LIKE CONCAT(CONCAT('%',#{must_read_title}),'%')
AND comment_content LIKE CONCAT(CONCAT('%',#{comment_content}),'%')
and comment_time >= #{start_date}
and comment_time <= #{end_date}
ORDER BY comment_time DESC LIMIT #{offset},#{pagesize}

		SELECT count(*) 
        FROM wkrj_like
        WHERE 1=1
        
			AND user_id = #{user_id}
		
		
			AND must_read_id = #{must_read_id}
