mapper.select:
SELECT na_id,na_title,na_content,na_other,na_inputuser,na_inputuserdept, DATE_FORMAT(na_inputtime,'%Y-%m-%d %H:%i:%s') AS na_inputtime,importance, accept_user,user_realname, (select dept_name from wkrj_sys_department where dept_id = na_inputuserdept) as dname FROM wkrj_noticeannounce n,wkrj_sys_user u
WHERE u.user_id = n.na_inputuser
AND na_title LIKE CONCAT(CONCAT('%',#{na_title}),'%')
and na_inputtime >= #{na_inputtime}
and na_inputtime <= #{end_date}
and (FIND_IN_SET(#{user_id},accept_user) or n.na_inputuserdept LIKE CONCAT('%',#{user_dept},'%'))
ORDER BY na_inputtime DESC LIMIT #{offset},#{rows}

mapper.update:
UPDATE wkrj_noticeannounce SET na_title = #{na_title},na_content = #{na_content},
na_other = #{na_other},na_inputuser = #{na_inputuser},na_inputuserdept = #{na_inputuserdept},
importance = #{importance},accept_user = #{accept_user}
WHERE na_id = #{na_id}

mapper.select:
SELECT * FROM wkrj_noticeannouncefile

     WHERE file_id=#{file_id}


     WHERE na_id=#{na_id}

mapper.update:
UPDATE wkrj_noticeannouncefile SET file_yname = #{file_yname},file_xname = #{file_xname},
file_inputtime = #{file_inputtime},file_inputuser = #{file_inputuser}

WHERE file_id = #{file_id}

mapper.delete:
		DELETE FROM wkrj_noticeannouncefile 
		
             WHERE file_id=#{file_id}
        
        
             WHERE na_id=#{na_id}
